#include <stdint.h>

#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

int main(void)
{
   uint32_t volatile *pClkCtrlReg = (uint32_t*) 0x40023830;
   uint32_t volatile *pPortDModeReg = (uint32_t*) 0x40020C00;
   uint32_t volatile *pPortDOutReg = (uint32_t*) 0x40020C14;
   uint32_t volatile *pPortAModeReg = (uint32_t*) 0x40020000;
   uint32_t volatile *pPortAInReg = (uint32_t*) 0x40020010;

   // 1. Enable the clock for GPIOD, GPIOA peripheral in the AHB1ENR
   /* <detailed method>
   uint32_t temp = *ppClkCtrlReg; // read operation
   temp = temp | 0x08; // modify
   *pClkCtrlReg = temp; // write back
   */

   // below is a short-hand method for above 3 lines of code
   *pClkCtrlReg |= (1 << 3);
   *pClkCtrlReg |= (1 << 0);
   // 2. Configure the mode of the IO pin as output
   //a. clear the 24th and 25th bit positions (CLEAR)
   *pPortDModeReg &= ~(3 << 24);
   //b. make 24th bit position as 1 (SET)
   *pPortDModeReg |= (1 << 24);


   //Configure PA0 as input mode (GPIOA MODE Register)
   *pPortAModeReg &= ~(3 << 0);

		while(1)
		{
	//Read the pin status of the pin PA0 (GPIOA input data Register)
	uint8_t pinA0Status = (uint8_t)(*pPortAInReg & 0x1); // Zero out all bits exept bit 0


	if (pinA0Status)
	{
		// Turn on the LED

		*pPortDOutReg |= (1 << 12);
	}
	else{
		// Turn off the LED
		*pPortDOutReg &= ~(1 << 12);

	}
}

}
